Scripts.OnMouseUp:
if ( this.isMoving ) then
this:StopMovingOrSizing();
-- DecursiveAfflictedListFrame:StopMovingOrSizing();
this.isMoving = false;
end

Scripts.OnMouseDown:
if ( ( ( not this.isLocked ) or ( this.isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) then
this:StartMoving();
-- DecursiveAfflictedListFrame:StartMoving();
this.isMoving = true;
end
if (arg1 == "RightButton") then
Dcr_ShowHideButtons();
end

Scripts.OnHide:
if ( this.isMoving ) then
this:StopMovingOrSizing();
-- DecursiveAfflictedListFrame:StopMovingOrSizing();
this.isMoving = false;
end

Scripts.OnShow:
local _, faction = UnitFactionGroup("player");
if (faction == DCR_ALLIANCE_NAME) then
DecursivePopulateListFrameOther:SetText(DCR_LOC_CLASS_PALADIN);
DecursivePopulateListFrameOther.ClassType = DCR_CLASS_PALADIN;
else
DecursivePopulateListFrameOther:SetText(DCR_LOC_CLASS_SHAMAN);
DecursivePopulateListFrameOther.ClassType = DCR_CLASS_SHAMAN;
end

if (Dcr_Saved.Print_ChatFrame) then
this:SetChecked(1);
else
this:SetChecked(0);
end

Scripts.OnClick:
Dcr_Saved.Print_CustomFrame = (this:GetChecked() == 1);
if (Dcr_Saved.Print_CustomFrame) then
DcrOptionsFrameAnchor:Enable();
else
DcrOptionsFrameAnchor:Disable();
end

Scripts.OnShow:
if (Dcr_Saved.Print_CustomFrame) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.Check_For_Abolish) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.DoNot_Blacklist_Prio_List) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.AlwaysUseBestSpell) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.Random_Order) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.Scan_Pets) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.Ingore_Stealthed) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.PlaySound) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.Print_Error) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.AfflictionTooltips) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.ReverseLiveDisplay) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.Hide_LiveList) then
this:SetChecked(1);
else
this:SetChecked(0);
end

if (Dcr_Saved.LiveListTied) then
this:SetChecked(1);
else
this:SetChecked(0);
end

Scripts.OnLoad:
Dcr_ThisSetText(DCR_LOC_AF_TYPE [DCR_MAGIC]);
this.CureFunction = Dcr_Cure_Magic;
this.CureType = DCR_MAGIC;
this.CurePos = false;
Dcr_CureTypeCheckBoxes[DCR_MAGIC] = this;

Scripts.OnShow:
if (Dcr_Saved.CureMagic) then
this:SetChecked(1);
else
this:SetChecked(0);
end
if (DCR_CAN_CURE_MAGIC or DCR_CAN_CURE_ENEMY_MAGIC) then
this:Enable();
else
this:SetChecked(0);
this:Disable();
end
Dcr_On_CureOrderCheckBox_Update(this);

