	WIM_HistoryViewNameScrollBar_Update();
	WIM_HistoryViewFiltersScrollBar_Update();
else
	WIM_HistoryFrame:Show();
end

if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
else
	PlaySound("igMainMenuOptionCheckBoxOn");
end
